CREATE TABLE Sys_DataAcquisition --数据采集 一次展示4条数据。
(
	id int identity (1,1) primary KEY,
	DeviceName varchar(50)  NULL,--设备名
	DeviceRunStatus varchar(50) NULL,--设备运行状态
