	FeedSpeed INT,--进给速度
	SpindleRatio INT, -- 主轴倍率
	FeedRatio INT, --进给倍率
	LoadRatio INT, --负载
	CreationTime DATETIME not null default getdate(),--创建时间
	IsEffective int DEFAULT 1 -- 0 无效 1 有效 1显示
)

